import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongDetails } from '../actions/songActions';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { getOrderDetails, payOrder, deliverOrder } from '../actions/orderActions'
import { ORDER_PAY_RESET, ORDER_DELIVER_RESET } from '../constants/orderConstants'
import { getUserDetails, updateUserProfile } from '../actions/userActions'
import { USER_UPDATE_PROFILE_RESET } from '../constants/userConstants'
import { listMyOrders } from '../actions/orderActions'
import Song from '../components/Song'
import Paginate from '../components/Paginate'
import SongCarousel from '../components/SongCarousel'
import { listSongs } from '../actions/songActions'
import '../styles/Header.css'

function SongScreen({ match, history}) {

    
  const [qty, setQty] = useState(1);
  const [liked, setLiked] = useState(false);

  const dispatch = useDispatch();

  const songDetails = useSelector(state => state.songDetails);
  const {  song, error, loading} = songDetails;


  useEffect(() => {
    dispatch(listSongDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cartsong/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/liked/${match.params.id}?qty=${qty}`);
    setLiked(true); // Update the state to indicate that the user has liked the song

  };

  const addToPreorder = () => {
    history.push(`/playlist/${match.params.id}?qty=${qty}`);
  };
  const [isHovered1, setIsHovered1] = useState(false);
  const [isHovered2, setIsHovered2] = useState(false);
  
  const handleMouseEnter1 = () => setIsHovered1(true);
  const handleMouseLeave1 = () => setIsHovered1(false);
  const handleMouseEnter2 = () => setIsHovered2(true);
  const handleMouseLeave2 = () => setIsHovered2(false);
  
  const [currentAudio, setCurrentAudio] = useState(null);
  let audioElement1, audioElement2;
  
  const handlePlayClick = (audioElement, stopAudioElement) => {
    // Stop the currently playing audio element, if any
    if (stopAudioElement) {
      stopAudioElement.pause();
    }
  
    // Set the new audio element as the currently playing one
    setCurrentAudio(audioElement);
  
    // Play the new audio element
    audioElement.play();
  };
  

  const [showPremium, setShowPremium] = useState(false);
  // const [showBasic, setShowBasic] = useState(true);

  const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [message, setMessage] = useState('')

    // const dispatch = useDispatch()

    const userDetails = useSelector(state => state.userDetails)
    const { user } = userDetails

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin

    const userUpdateProfile = useSelector(state => state.userUpdateProfile)
    const { success } = userUpdateProfile

    const orderListMy = useSelector(state => state.orderListMy)
    const { loading: loadingOrders, error: errorOrders, orders } = orderListMy

    const [showDownload, setShowDownload] = useState(false)

    useEffect(() => {
        if (!userInfo) {
            history.push('/login')
        } else {
            if (!user || !user.name || success || userInfo._id !== user._id) {
                dispatch({ type: USER_UPDATE_PROFILE_RESET })
                dispatch(getUserDetails('profile'))
                dispatch(listMyOrders())
            } else {
                setName(user.name)
                setEmail(user.email)
            }
        }
    }, [dispatch, history, userInfo, user, success])

  function carousel(){
    const dispatch = useDispatch()
    const songList = useSelector(state => state.songList)
    const { error, loading, songs, page, pages } = songList

    let keyword = history.location.search

    useEffect(() => {
        dispatch(listSongs(keyword))
    }, [dispatch, keyword])

    return (
      <div>
          <h1>sadsa</h1>
          {!keyword && <SongCarousel />}
          {loading
              ? <Loader />
              : error
                  ? <Message variant='danger'>{error}</Message>
                  : (
                      <div>
                          <Row>
                              {songs.map(song => (
                                  <Col key={song._id} sm={12} md={6} lg={4} xl={3}>
                                      <Song song={song} />
                                  </Col>
                              ))}
                          </Row>
                          <Paginate page={page} pages={pages} keyword={keyword} className="home-screen-pagination" />
                      </div>
                  )}
      </div>
  )
  }
  
  return (
    <div>
      <SongCarousel />
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
     ) : error ? ( 
        <Message variant="danger">{error}</Message>
     ) : ( 
        <div>
          <Row>
          <Col
  sm={12}
  md={6}
  lg={4}
  xl={3}
  className="home-screen-product-col"
>
  <Image src={song.image} alt={song.name} fluid />
  <br />
  <br />
  <Card>
    <ListGroup>
      <ListGroup.Item>
        <Row onMouseEnter={handleMouseEnter1} onMouseLeave={handleMouseLeave1}>
          <h4>{song.title}</h4>
          {song.music && (
            <audio
              controls
              className="music-player"
              style={{ display: isHovered1 ? "block" : "none" }}
              onClick={() =>
                handlePlayClick(audioElement1, audioElement2)
              }
            >
              <source src={song.music} type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          )}
        </Row>
        <audio ref={(element) => { audioElement1 = element; }} />
      </ListGroup.Item>
      <ListGroup.Item>
        <Row onMouseEnter={handleMouseEnter2} onMouseLeave={handleMouseLeave2}>
          <h4>{song.title2}</h4>
          {song.music2 && (
            <audio
              controls
              className="music-player"
              style={{ display: isHovered2 ? "block" : "none" }}
              onClick={() =>
                handlePlayClick(audioElement2, audioElement1)
              }
            >
              <source src={song.music2} type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          )}
        </Row>
        <audio ref={(element) => { audioElement2 = element; }} />
      
      </ListGroup.Item>
    </ListGroup>
  </Card>
</Col>
     
            <Col md={5}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h1>{song.name}</h1>
                </ListGroup.Item>

                <ListGroup.Item>
                  Description: {song.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup >


                  {/* {song.countInStock > 0 && (
                    <ListGroup.Item>
                      <Row>

                      </Row>
                    </ListGroup.Item>
                  )} */}

                  <ListGroup.Item>
                  
         
         {loadingOrders ? (
  <Loader /> 
) : errorOrders ? ( 
  <Message variant='danger'>{errorOrders}</Message> 
) : ( 
  orders.map(order => (
    <div key={order.id}>
      <h1>
        {order.isBasic && !showPremium && (

<ListGroup.Item>
<div className="text-center">
<Button
                  onClick={addToWishlist}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0 || liked} // Disable the button if the song is out of stock or if the user has already liked it
                ><FontAwesomeIcon icon={faHeart} className="me-2" />
                {liked ? "Liked" : "Like"} {/* Change the button text based on the state */}</Button>
</div>
          </ListGroup.Item>
        )}
      </h1>

      <h2>
        {order.isPremium && (
          <div>

<ListGroup.Item>
<div className="text-center">
              <Button
                  onClick={addToWishlist}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0 || liked} // Disable the button if the song is out of stock or if the user has already liked it
                ><FontAwesomeIcon icon={faHeart} className="me-2" />
                {liked ? "Liked" : "Like"} {/* Change the button text based on the state */}</Button>
                </div>
                </ListGroup.Item>
                <ListGroup.Item>
                <div className="text-center">
                <Button
                  onClick={addToWishlist}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0 || liked} // Disable the button if the song is out of stock or if the user has already liked it
                >Download</Button>
</div>
            </ListGroup.Item>

          </div>
        )}
      </h2>
    </div>
  ))

  
)}

        


                      </ListGroup.Item>
                      {/* {userInfo ? (
            <h1></h1>
              ) : (
                <ListGroup.Item>
                  <Link to="/login">
                    <Message variant="info">
                      Please subscribe to like songs
                    </Message>
                  </Link>
                </ListGroup.Item>
              )} */}
         <div className="text-center">
              <ListGroup.Item>
                <Button
                  onClick={addToPreorder}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  Add to Playlist
                </Button>
              </ListGroup.Item>

              </div>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
   )} 
</div>

);
}

export default SongScreen;
