import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongDetails } from '../actions/songActions';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function SongScreen({ match, history }) {
  const [qty, setQty] = useState(1);

  const dispatch = useDispatch();

  const songDetails = useSelector(state => state.songDetails);
  const { loading, error, song } = songDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    dispatch(listSongDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cartsong/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/wishlist/${match.params.id}?qty=${qty}`);
  };

  const addToPreorder = () => {
    history.push(`/preorder/${match.params.id}?qty=${qty}`);
  };

  return (
    <div className="song-container">
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
            <Col md={6} className="song-info">
              <div className="song-image">
                <Image src={song.image} alt={song.name} fluid />
              </div>
              <div className="song-title">
                <h4>{song.name}</h4>
              </div>
              <div className="song-audio">
                <audio controls className="music-player">
                  <source src={song.music} type="audio/mp3" />
                  Your browser does not support the audio element.
                </audio>
              </div>
              <div className="song-description">
                <p>{song.description}</p>
              </div>
            </Col>

            <Col md={3} className="song-list">
              <div className="song-list-group">
                <ListGroup variant="flush">
                  <ListGroup.Item>
                    <h4>More by {song.artist}</h4>
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <Link to={`/artist/${song.artistId}`}>
                      <span>{song.artist}</span>
                    </Link>
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <Link to={`/album/${song.albumId}`}>
                      <span>{song.album}</span>
                    </Link>
                  </ListGroup.Item>
                </ListGroup>
              </div>
            </Col>

            <Col md={3} className="song-actions">
              <div className="song-actions-card">
                <Card>
                  <ListGroup>
                    <ListGroup.Item>
                      <Button
                        onClick={addToCartHandler}
                        className="btn-block"
                        type="button"
                        disabled={song.countInStock === 0}
                      >
                        Download
                      </Button>
                    </ListGroup.Item>
                    {userInfo ? (
                      <ListGroup.Item>
                        <Button
                          onClick={addToWishlist}
                          className="btn-block"
                          type="button"
                          disabled={song.countInStock === 0}
                        >
<FontAwesomeIcon icon={faHeart} className="mr-2" />
                          Add to Wishlist
                        </Button>
                      </ListGroup.Item>
                    ) : null}
                    {userInfo ? (
                      <ListGroup.Item>
                        <Button
                          onClick={addToPreorder}
                          className="btn-block"
                          type="button"
                          disabled={song.countInStock === 0}
                        >
                          Pre-order
                        </Button>
                      </ListGroup.Item>
                    ) : null}
                  </ListGroup>
                  <div className="song-price">
                    <div className="price-box">
                      <span className="price-label">Price:</span>
                      <span className="price-value">${song.price}</span>
                    </div>
                  </div>
                  <div className="song-rating">
                    <Rating
                      value={song.rating}
                      text={`${song.numReviews} reviews`}
                    />
                  </div>
                  <div className="song-qty">
                    <div className="qty-label">Qty:</div>
                    <div className="qty-select">
                      <Form.Control
                        as="select"
                        value={qty}
                        onChange={e => setQty(e.target.value)}
                      >
                        {[...Array(song.countInStock).keys()].map(x => (
                          <option key={x + 1} value={x + 1}>
                            {x + 1}
                          </option>
                        ))}
                      </Form.Control>
                    </div>
                  </div>
                </Card>
              </div>
            </Col>
          </Row>
        </div>
      )}
    </div>
  );
}
export default SongScreen;
