import React, { useState, useEffect } from 'react';
import { Row, Col } from 'react-bootstrap';
import Song from '../components/Song';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongs } from '../actions/songActions';
import { useDispatch, useSelector } from 'react-redux';

import Paginate from '../components/Paginate'
import SongCarousel from '../components/SongCarousel'
// import { listSongs } from '../actions/songActions'
import '../styles/Header.css'
function SongListScreen({history}) {
  const dispatch = useDispatch()
  const songList = useSelector(state => state.songList)
  const { error, loading, songs, page, pages } = songList

  let keyword = history.location.search

  useEffect(() => {
      dispatch(listSongs(keyword))
  }, [dispatch, keyword])

  return (
    <div>
            <h1>sadsa</h1>
            {!keyword && <SongCarousel />}
            {loading
                ? <Loader />
                : error
                    ? <Message variant='danger'>{error}</Message>
                    : (
                        <div>
                            <Row>
                                {songs.map(song => (
                                    <Col key={song._id} sm={12} md={6} lg={4} xl={3}>
                                        <Song song={song} />
                                    </Col>
                                ))}
                            </Row>
                            <Paginate page={page} pages={pages} keyword={keyword} className="home-screen-pagination" />
                        </div>
                    )}
        </div>
  );
}

export default SongListScreen;
