import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, ListGroupItem } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listPlanDetails } from '../actions/planActions';

function PlanScreen({ match, history }) {
  const [qty, setQty] = useState(1);
  
  const dispatch = useDispatch();
  
  const planDetails = useSelector(state => state.planDetails);
  const { loading, error, plan } = planDetails;

  useEffect(() => {
    dispatch(listPlanDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`);
  };

  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
            {/* <Col md={6}>
              <Image src={plan.image} alt={plan.name} fluid />
            </Col> */}
            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  {/* <h3>{plan.name}</h3> */}
                </ListGroup.Item>
                <ListGroup.Item>
                  <Rating
                    // value={plan.rating}
                    // text={`${plan.numReviews} reviews`}
                    color={"#f8e825"}
                  />
                </ListGroup.Item>
                <ListGroup.Item>Price: ${plan.price}</ListGroup.Item>
                <ListGroup.Item>
                  Description: {plan.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>
            <Col md={3}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroup.Item>
                    <Row>
                      <Col>Price:</Col>
                      <Col>
                        <strong>${plan.price}</strong>
                      </Col>
                    </Row>
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <Row>
                      <Col>Status:</Col>
                      <Col>
                        {plan.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
                      </Col>
                    </Row>
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex justify-content-center">
                    {plan.countInStock ? (
                      <Button
                        type="button"
                        className="btn-block"
                        onClick={addToCartHandler}
                      >
                        Buy
                      </Button>
                    ) : (
                      <Button
                        type="button"
                        className="btn-block"
                        disabled
                      >
                        Out of Stock
                      </Button>
                    )}
                  </ListGroup.Item>
                </ListGroup>
              </Card>
            </Col>
          </Row>
        </div>
      )}
    </div>
  );
}

export default PlanScreen;
