import React, { useState, useEffect } from 'react';
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import CheckoutSteps from '../components/CheckoutSteps';
import { createPlan } from '../actions/planActions';
import { PLAN_CREATE_RESET } from '../constants/planConstants';

function PlaceOrderScreen({ history }) {
  const orderCreate = useSelector((state) => state.orderCreate);
  const { order, error, success } = orderCreate;
  const dispatch = useDispatch();

  const cart = useSelector((state) => state.cart);

//   cart.planPrice = cart.cartPlans.reduce((acc, plan) => acc + plan.price * plan.qty, 0).toFixed(2);
  cart.taxPrice = Number((0.082) * cart.planPrice).toFixed(2);
  cart.totalPrice = (Number(cart.planPrice) + Number(cart.taxPrice)).toFixed(2);

  if (!cart.paymentMethod) {
    history.push('/payment');
  }

  useEffect(() => {
    if (success) {
      history.push(`/plan/${order._id}`);
      dispatch({ type: PLAN_CREATE_RESET });
    }
  }, [success, history]);

  const placeOrder = () => {
    dispatch(
      createPlan({
        orderPlans: cart.cartPlans,
        paymentMethod: cart.paymentMethod,
        planPrice: cart.planPrice,
        taxPrice: cart.taxPrice,
        totalPrice: cart.totalPrice,
      })
    );
  };

  return (
    <div>
      <CheckoutSteps step1 step2 step3 step4 />
      <Row>
        <Col md={8}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>Contact</h2>

              <p>
                <strong>Details: </strong>
                {cart.shippingAddress.address}, {cart.shippingAddress.city} {'  '}
                {cart.shippingAddress.postalCode}, {'  '}
                {cart.shippingAddress.country}
              </p>
            </ListGroup.Item>

            <ListGroup.Item>
              <h2>Payment Method</h2>
              <p>
                <strong>Method: </strong>
                {cart.paymentMethod}
              </p>
            </ListGroup.Item>

            <ListGroup.Item>
              <h2>Order Plans</h2>
              {cart.cartPlans.length === 0 ? (
                <Message variant="info">Your cart is empty</Message>
              ) : (
                <ListGroup variant="flush">
                  {cart.cartPlans.map((plan) => (
                    <ListGroup.Item key={plan.plan}>
                      <Row>
                        <Col md={1}>
                          <Image src={plan.image} alt={plan.name} fluid rounded />
                        </Col>
                        <Col>
                          <Link to={`/plan/${plan.plan}`}>{plan.name}</Link>
                        </Col>
                        <Col md={4}>
                          {plan.qty} X ${plan.price} = ${(plan.qty * plan.price).toFixed(2)}
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>

        <Col md={4}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h2>Order Summary</h2>
              </ListGroup.Item>

              <ListGroup.Item>
                <Row>
                  <Col>Plans:</Col>
                  <Col>${cart.planPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
            <Row>
              <Col>Tax:</Col>
              <Col>${cart.taxPrice}</Col>
            </Row>
          </ListGroup.Item>

          <ListGroup.Item>
            <Row>
              <Col>Total:</Col>
              <Col>${cart.totalPrice}</Col>
            </Row>
          </ListGroup.Item>

          <ListGroup.Item>
            {error && <Message variant="danger">{error}</Message>}
          </ListGroup.Item>

          <ListGroup.Item>
            <Button
              type="button"
              className="btn-block"
              disabled={cart.cartPlans === 0}
              onClick={placeOrder}
            >
              Place Order
            </Button>
          </ListGroup.Item>
        </ListGroup>
      </Card>
    </Col>
  </Row>
</div>
);
}

export default PlaceOrderScreen;
