import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createPlaylist, deletePlaylist, listPlaylists } from '../actions/playlistActions';

const Playlist = () => {
  const [title, setTitle] = useState('');
  const [image, setImage] = useState(null);
  const dispatch = useDispatch();
  const { loading, error, playlists } = useSelector((state) => state.playlistList);

  useEffect(() => {
    dispatch(listPlaylists());
  }, [dispatch]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(createPlaylist({ title, image }));
  };

  const deleteHandler = (id) => {
    dispatch(deletePlaylist(id)).then(() => {
      dispatch(listPlaylists());
    });
  };

  return (
    <div>
      <h1>Create Playlist</h1>
      <form onSubmit={submitHandler}>
        <div>
          <label htmlFor='title'>Title</label>
          <input
            type='text'
            id='title'
            placeholder='Enter playlist title'
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor='image'>Image</label>
          <input
            type='file'
            id='image'
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>
        <button type='submit'>Create Playlist</button>
      </form>
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <div>
          <h2>Playlist List</h2>
          <ul>
            {playlists.map((playlist) => (
              <li key={playlist.id}>
                {playlist.title}
                {playlist.image && (
                  <img src={playlist.image} alt={playlist.title} />
                )}
                <button onClick={() => deleteHandler(playlist.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Playlist;
