import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addToWishlist, removeFromWishlist } from '../actions/wishActions';

const WishScreen = ({ match, location, history }) => {
    const productId = match.params.id;
    const qty = location.search ? Number(location.search.split('=')[1]) : 1;
    const dispatch = useDispatch();

    const wishlist = useSelector(state => state.wishlist);
    const { wishlistItems } = wishlist;

    useEffect(() => {
        if (productId) {
            dispatch(addToWishlist(productId, qty));
        }
    }, [dispatch, productId, qty]);

    const removeFromWishlistHandler = (id) => {
        dispatch(removeFromWishlist(id));
    };

    return (
        <div>
            <h1>Wishlist</h1>
            {wishlistItems.length === 0 ? (
                <div>
                    Your wishlist is empty <Link to='/'>Go Back</Link>
                </div>
            ) : (
                <ul>
                    {wishlistItems.map(item => (
                        <li key={item.product}>
                            <div>
                                <img src={item.image} alt={item.name} />
                            </div>
                            <div>
                                <Link to={`/product/${item.product}`}>{item.name}</Link>
                            </div>
                            <div>${item.price}</div>
                            <div>
                                <button onClick={() => removeFromWishlistHandler(item.product)}>Remove</button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default WishScreen;
