import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col } from 'react-bootstrap'
import Plan from '../components/Plan'
import Loader from '../components/Loader'
import Message from '../components/Message'
import Paginate from '../components/Paginate'
import ProductCarousel from '../components/ProductCarousel'
import { listPlans } from '../actions/planActions'
import '../styles/Header.css'

function HomeScreen({ history}) {
  const dispatch = useDispatch()
  const planList = useSelector(state => state.planList)
  const { error, loading, plans, page, pages } = planList
  // console.log('plans:', plans); // Add this line to check the value of plans

  let keyword = history.location.search

  useEffect(() => {
    dispatch(listPlans(keyword))
  }, [dispatch, keyword])

  return (
    <div className="home-screen-container">
      {/* {!keyword && <ProductCarousel />} */}

      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <div className="home-screen-product-list-container">
          <Row className="home-screen-product-list">
          {plans.map(plan => (
                          <Col key={plan._id} sm={12} md={6} lg={4} xl={3} className="home-screen-product-col">
                <Plan plan={plan} />
              </Col>
            ))}
          </Row>
          {/* <Paginate page={page} pages={pages} keyword={keyword} className="home-screen-pagination" /> */}
        </div>
      )}
    </div>
  )
}

export default HomeScreen
