import { ADD_MESSAGE } from '../constants/chatboxConstants';

export const addMessage = (text) => (dispatch, getState) => {
  const userInfo = getState().userLogin.userInfo;
  const name = userInfo ? userInfo.name : 'Anonymous';
  const friends = userInfo ? userInfo.friends : [];
  if (friends.includes(text.recipientId)) {
    dispatch({
      type: ADD_MESSAGE,
      payload: { name, text }
    });
  } else {
    alert('You are not friends with this user!');
  }
};
