import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage } from '../actions/chatboxActions';
import io from 'socket.io-client';

const socket = io('http://localhost:3000/'); // replace with your server URL

function Chatbox() {
  const [message, setMessage] = useState('');

  const dispatch = useDispatch();

  const chatbox = useSelector(state => state.chatbox);
  const { messages } = chatbox;

  const userInfo = useSelector(state => state.userLogin.userInfo);

  useEffect(() => {
    // listen for incoming messages from the server
    socket.on('message', (message) => {
      dispatch(addMessage(message));
    });

    // load initial messages from the server
    socket.emit('getMessages');
    socket.on('messages', (messages) => {
      dispatch(setMessages(messages));
    });
  }, [dispatch]);

  const submitHandler = (e) => {
    e.preventDefault();
    socket.emit('message', { name: userInfo.name, text: message });
    setMessage('');
  };

  return (
    <div>
      <h2>Chatbox</h2>
      {userInfo ? (
        <form onSubmit={submitHandler}>
          <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
          <button type="submit">Send</button>
        </form>
      ) : (
        <p>Please log in to join the chat.</p>
      )}
      <ul>
        {messages.map((message, index) => (
          <li key={index}>
            <strong>{message.name}</strong>: {message.text}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Chatbox;
