import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form, ListGroupItem } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listProductDetails, createProductReview } from '../actions/productActions';
import { PRODUCT_CREATE_REVIEW_RESET } from '../constants/productConstants';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { getOrderDetails, payOrder, deliverOrder } from '../actions/orderActions'
import { ORDER_PAY_RESET, ORDER_DELIVER_RESET } from '../constants/orderConstants'
import axios from 'axios';
import { listMyOrders } from '../actions/orderActions';
import { ORDER_LIST_MY_RESET } from '../constants/orderConstants';
function ProductScreen({ match, history }) {
  const [qty, setQty] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [wishlist, setWishlist] = useState([]);
  const [bought, setBought] = useState(false);
  const [owned, setOwned] = useState(false);
  const [boughtQty, setBoughtQty] = useState(1); // new state variable to track the number of products the user has already bought

  const orderId = match.params.id
  const dispatch = useDispatch()


  const [sdkReady, setSdkReady] = useState(false)

  const orderDetails = useSelector(state => state.orderDetails)
  const { order} = orderDetails

  const orderPay = useSelector(state => state.orderPay)
  const { loading: loadingPay, success: successPay } = orderPay

  const orderDeliver = useSelector(state => state.orderDeliver)
  const { loading: loadingDeliver, success: successDeliver } = orderDeliver


  const productDetails = useSelector(state => state.productDetails);
  const { loading, error, product } = productDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;


  const productReviewCreate = useSelector(state => state.productReviewCreate);
  const {
    loading: loadingProductReview,
    error: errorProductReview,
    success: successProductReview,
  } = productReviewCreate;

  useEffect(() => {
    if (successProductReview) {
      setRating(0);
      setComment('');
      dispatch({ type: PRODUCT_CREATE_REVIEW_RESET });
    }

    dispatch(listProductDetails(match.params.id));
  }, [dispatch, match, successProductReview]);

  const addPayPalScript = () => {
    const script = document.createElement('script')
    script.type = 'text/javascript'
    script.src = 'https://www.paypal.com/sdk/js?client-id=AdBpHPdb251WJxSmZV3FxvQDXQZN01hrF0albnLIrH5JmIQlCv5eWa46flZdI2pdpLOF22LKgxBE3FSy'
    script.async = true
    script.onload = () => {
        setSdkReady(true)
    }
    document.body.appendChild(script)
}


  useEffect(() => {

    if (!userInfo) {
        history.push('/login')
    }

    if (!order || successPay || order._id !== Number(orderId) || successDeliver) {
        dispatch({ type: ORDER_PAY_RESET })
        dispatch({ type: ORDER_DELIVER_RESET })

        dispatch(getOrderDetails(orderId))
    } else if (!order.isPaid) {
        if (!window.paypal) {
            addPayPalScript()
        } else {
            setSdkReady(true)
        }
    }
}, [dispatch, order, orderId, successPay, successDeliver])


  useEffect(() => {
    dispatch({ type: ORDER_LIST_MY_RESET }); // reset the orders state when the component mounts
    dispatch(listMyOrders(order)); // call the API to get the user's orders
  }, [dispatch]);

  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`);
  };



  // read the user's cart from local storage, or create a new one if it doesn't exist
const cart = JSON.parse(localStorage.getItem('cart')) || {};

// check if the user has bought this product before
const isBought = cart[match.params.id]?.isBought;

// update the isBought property for this product in the user's cart
const updateCart = () => {
  const updatedCart = { ...cart };
  updatedCart[match.params.id] = { ...updatedCart[match.params.id], isBought: true };
  localStorage.setItem('cart', JSON.stringify(updatedCart));
  setBought(true);
  history.push(`/cart/${match.params.id}?qty=${qty}`);

};

// check if the user has already bought this product
useEffect(() => {
  const cart = JSON.parse(localStorage.getItem('cart')) || {};
  const isBought = cart[match.params.id]?.isBought;
  setBought(isBought);
}, [match.params.id]);

// check if the user owns this product (i.e. has already bought it and has not returned it)
useEffect(() => {
  const cart = JSON.parse(localStorage.getItem('cart')) || {};
  const owned = cart[match.params.id]?.owned;
  setOwned(owned);
}, [match.params.id]);

// check if the user can buy this product
const canBuy = !bought || owned;


  return (
    <div>
      <Link to="/plan" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
            <Col md={6}>
              <Image src={product.image} alt={product.name} fluid />
            </Col>



            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h3>{product.name}</h3>
                </ListGroup.Item>


                <ListGroup.Item>
                  Description: {product.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroup.Item>


                                                <Row>
                                                    <Col>Price:</Col>
                                                    <Col>
                                                        <strong>${product.price}</strong>
                                                    </Col>
                                                </Row>
                                            </ListGroup.Item>

                                            <ListGroup.Item className="d-flex justify-content-center">
  {/* conditionally render the button based on whether the user has bought the product and the product's availability in stock */}
  <Button
  className="btn-block"
  type="button"
  disabled={!canBuy}
  onClick={updateCart}
>
  {owned ? 'Owned' : bought ? 'Bought' : 'Buy Now'}
</Button>





</ListGroup.Item>



              </ListGroup>
            </Card>
        </Col>

        </Row>
  <Row>

  </Row>
</div>
)}
</div>
);
}
export default ProductScreen;
