import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongDetails } from '../actions/songActions';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function SongScreen({ match, history }) {
  const [qty, setQty] = useState(1);

  const dispatch = useDispatch();

  const songDetails = useSelector(state => state.songDetails);
  const { loading, error, song } = songDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    dispatch(listSongDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cartsong/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/wishlist/${match.params.id}?qty=${qty}`);
  };

  const addToPreorder = () => {
    history.push(`/preorder/${match.params.id}?qty=${qty}`);
  };

  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
          

     
            <Col md={5}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h1>{song.name}</h1>
                </ListGroup.Item>

                <ListGroup.Item>
                  Description: {song.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup >


                  {song.countInStock > 0 && (
                    <ListGroup.Item>
                      <Row>

                      </Row>
                    </ListGroup.Item>
                  )}

                  <ListGroup.Item>
                    <Button
                      onClick={addToCartHandler}
                      className="btn-block"
                      type="button"
                      disabled={song.countInStock === 0}
                      >
                      Download
                      </Button>
                      </ListGroup.Item>
                      {userInfo ? (
                <ListGroup.Item>
                  <Button
                    onClick={addToWishlist}
                    className="btn-block"
                    type="button"
                    disabled={song.countInStock === 0}
                  >
                    <FontAwesomeIcon icon={faHeart} className="me-2" />
                    Like
                  </Button>
                </ListGroup.Item>
              ) : (
                <ListGroup.Item>
                  <Link to="/login">
                    <Message variant="info">
                      Please login to add to wishlist
                    </Message>
                  </Link>
                </ListGroup.Item>
              )}

              <ListGroup.Item>
                <Button
                  onClick={addToPreorder}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  Add to Playlist
                </Button>
              </ListGroup.Item>

              
            </ListGroup>

            <Col  sm={12}
              md={6}
              lg={4}
              xl={3}
              className="home-screen-product-col">
              
              <Image src={song.image} alt={song.name} fluid />
              <br/>
              <br/>
              <Card>

              <ListGroup >



  <ListGroup.Item>
    <Row>

              <h4>{song.title}</h4>
              <audio controls className="music-player">
          <source src={song.music} type="audio/mp3" />
          Your browser does not support the audio element.
        </audio>
        <br/>
       </Row>
       </ListGroup.Item>

       <ListGroup.Item>
    <Row>
        <h4>{song.title2}</h4>
              <audio controls className="music-player">
          <source src={song.music2} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>
        </Row>
       </ListGroup.Item>

    
  
        <ListGroup.Item>
    <Row>

        <h4>{song.title3}</h4>
              <audio controls className="music-player">
          <source src={song.music3} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>
        </Row>
       </ListGroup.Item>
       
        <ListGroup.Item>
    <Row>

        <h4>{song.title4}</h4>
              <audio controls className="music-player">
          <source src={song.music4} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>
        </Row>
       </ListGroup.Item>
       <ListGroup.Item>
    <Row>
        <h4>{song.title5}</h4>
<audio controls className="music-player">
  <source src={song.music5} type="audio/mp3" />
  Your browser does not support the audio element.
</audio>
</Row>
       </ListGroup.Item>
</ListGroup>
</Card>
            </Col>
          </Card>
        </Col>
      </Row>
    </div>
  )}
</div>

);
}

export default SongScreen;
