import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createPlaylist, deletePlaylist, listPlaylists } from '../actions/playlistActions';
import styled from 'styled-components';

const Playlist = () => {
  const [title, setTitle] = useState('');
  const [image, setImage] = useState(null);
  const dispatch = useDispatch();
  const { loading, error, playlists } = useSelector((state) => state.playlistList);

  useEffect(() => {
    dispatch(listPlaylists());
  }, [dispatch]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(createPlaylist({ title, image }));
  };

  const deleteHandler = (id) => {
    dispatch(deletePlaylist(id)).then(() => {
      dispatch(listPlaylists());
    });
  };

  return (
    <PlaylistContainer>
      <PlaylistHeader>Create Playlist</PlaylistHeader>
      <form onSubmit={submitHandler}>
        <PlaylistForm>
          <PlaylistFormItem>
            <label htmlFor='title'>Title</label>
            <PlaylistInput
              type='text'
              id='title'
              placeholder='Enter playlist title'
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </PlaylistFormItem>
          <PlaylistFormItem>
            <label htmlFor='image'>Image</label>
            <PlaylistInput
              type='file'
              id='image'
              onChange={(e) => setImage(e.target.files[0])}
            />
          </PlaylistFormItem>
          <PlaylistButton type='submit'>Create Playlist</PlaylistButton>
        </PlaylistForm>
      </form>
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <PlaylistList>
          <PlaylistHeader>Playlist List</PlaylistHeader>
          {playlists.map((playlist) => (
            <PlaylistListItem key={playlist.id}>
              <PlaylistImage src={playlist.image || 'https://via.placeholder.com/150'} alt={playlist.title} />
              <PlaylistInfo>
                <PlaylistTitle>{playlist.title}</PlaylistTitle>
                <PlaylistDeleteButton onClick={() => deleteHandler(playlist.id)}>DELETE</PlaylistDeleteButton>
              </PlaylistInfo>
            </PlaylistListItem>
          ))}
        </PlaylistList>
      )}
    </PlaylistContainer>
  );
};

export default Playlist;

const PlaylistContainer = styled.div`
  max-width: 600px;
  margin: 0 auto;
  padding: 50px 20px;
  background-color: #181818;
  color: #fff;
`;

const PlaylistHeader = styled.h1`
  font-size: 2rem;
  font-weight: bold;
  margin-bottom: 30px;
`;

const PlaylistForm = styled.div`
  display: flex;
  flex-direction: column;
`;

const PlaylistFormItem = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 20px;
`;

const PlaylistInput = styled.input`
  padding: 10px;
  background-color: #282828;
  border: none;
  border-radius: 5px;
  color: #fff;
  font-size: 1rem;
`;

const PlaylistButton = styled.button`
  padding: 10px;
  background-color: #1db954;
  border: none;
  border-radius: 5px;
  color: #fff;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  &:hover {
    background-color: #1ed760;
  }
  `;
