import { ADD_MESSAGE } from '../constants/chatboxConstants';

// const initialState = {
//   messages: []
// };

export const chatboxReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_MESSAGE:
      return {
        ...state,
        messages: [...state.messages, action.payload]
      };
    default:
      return state;
  }
};

const initialState = {
    friends: []
  };
  
  export const friendsListReducer = (state = initialState, action) => {
    switch (action.type) {
      case ADD_FRIEND:
        return {
          ...state,
          friends: [...state.friends, action.payload]
        };
      case REMOVE_FRIEND:
        return {
          ...state,
          friends: state.friends.filter(friend => friend._id !== action.payload)
        };
      default:
        return state;
    }
  };
