import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form, ListGroupItem } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listProductDetails, createProductReview } from '../actions/productActions';
import { PRODUCT_CREATE_REVIEW_RESET } from '../constants/productConstants';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { getOrderDetails, payOrder, deliverOrder } from '../actions/orderActions'
import { ORDER_PAY_RESET, ORDER_DELIVER_RESET } from '../constants/orderConstants'

function ProductScreen({ match, history }) {
  const [qty, setQty] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [wishlist, setWishlist] = useState([]);
  const [bought, setBought] = useState(false);
  const [owned, setOwned] = useState(false);
  const [boughtQty, setBoughtQty] = useState(1); // new state variable to track the number of products the user has already bought

  const orderDetails = useSelector(state => state.orderDetails)
  const { order } = orderDetails

  const dispatch = useDispatch();

  const productDetails = useSelector(state => state.productDetails);
  const { loading, error, product } = productDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;


  const productReviewCreate = useSelector(state => state.productReviewCreate);
  const {
    loading: loadingProductReview,
    error: errorProductReview,
    success: successProductReview,
  } = productReviewCreate;

  useEffect(() => {
    if (successProductReview) {
      setRating(0);
      setComment('');
      dispatch({ type: PRODUCT_CREATE_REVIEW_RESET });
    }

    dispatch(listProductDetails(match.params.id));
  }, [dispatch, match, successProductReview]);

  useEffect(() => {
    if (userInfo && product && product.owner && product.owner.includes(userInfo._id)) {
      setOwned(true);
    }

    // update the boughtQty state variable when the user buys a product
    if (history.location.search.includes('?qty=')) {
      const qtyParam = Number(history.location.search.split('=')[1]);
      setBought(true);
      setBoughtQty(qtyParam);
    }
  }, [userInfo, product, history]);

  const addToCartHandler = () => {
    // calculate the maximum allowed quantity based on the available quantity and the number of products the user has already bought
    const maxQty = product.countInStock - boughtQty;

    if (qty > maxQty) {
      alert(`You can buy up to ${maxQty} more product(s).`);
    } else {
      history.push(`/cart/${match.params.id}?qty=${qty}`);
      setBoughtQty(boughtQty + qty);
      setBought(true);
    }
  };





  return (
    <div>
      <Link to="/plan" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
            <Col md={6}>
              <Image src={product.image} alt={product.name} fluid />
            </Col>



            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h3>{product.name}</h3>
                </ListGroup.Item>


                <ListGroup.Item>
                  Description: {product.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroup.Item>


                                                <Row>
                                                    <Col>Price:</Col>
                                                    <Col>
                                                        <strong>${product.price}</strong>
                                                    </Col>
                                                </Row>
                                            </ListGroup.Item>

                                            <ListGroup.Item className="d-flex justify-content-center">
  {/* conditionally render the button based on whether the user has bought the product and the product's availability in stock */}
  <Button
  type="button"
  className="btn-block"
  disabled={bought || product.isBought || product.countInStock === 1 || (product.countInStock - boughtQty) === 1}
  onClick={addToCartHandler}
>
  {bought ? 'Already bought' : 'Buy'}
</Button>


</ListGroup.Item>



              </ListGroup>
            </Card>
        </Col>

        </Row>
  <Row>

  </Row>
</div>
)}
</div>
);
}
export default ProductScreen;
