import { ADD_MESSAGE, ADD_FRIEND, REMOVE_FRIEND } from '../constants/chatboxConstants';

const chatboxInitialState = {
  messages: []
};

export const chatboxReducer = (state = chatboxInitialState, action) => {
  switch (action.type) {
    case ADD_MESSAGE:
      return {
        ...state,
        messages: [...state.messages, action.payload]
      };
    default:
      return state;
  }
};

const friendsListInitialState = {
  friends: []
};

export const friendsListReducer = (state = friendsListInitialState, action) => {
  switch (action.type) {
    case ADD_FRIEND:
      return {
        ...state,
        friends: [...state.friends, action.payload]
      };
    case REMOVE_FRIEND:
      return {
        ...state,
        friends: state.friends.filter(friend => friend._id !== action.payload)
      };
    default:
      return state;
  }
};
