import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addToPreorder, removeFromPreorder } from '../actions/preorderActions';
import { Row, Col, Image, ListGroup, Button, Card, Form, Container } from 'react-bootstrap';
// import { Card } from 'react-bootstrap'
import { listProductDetails, createProductReview } from '../actions/productActions';
import Rating from '../components/Rating';

const PreorderScreen = ({ match, location, history }) => {

    return (
        <Container>
          {loading ? (
            <Loader />
          ) : error ? (
            <Message variant="danger">{error}</Message>
          ) : (
            <>
              <Col md={3}>
                <ListGroup variant="flush">
                  <ListGroup.Item>
                    <h3>{product && product.name}</h3> // add a check for product here
                  </ListGroup.Item>
      
                  <ListGroup.Item>
                    <Rating
                      value={product && product.rating} // add a check for product here
                      text={`${product && product.numReviews} reviews`} // add a check for product here
                      color={"#f8e825"}
                    />
                  </ListGroup.Item>
      
                  <ListGroup.Item>
                    Price: ${product && product.price} // add a check for product here
                  </ListGroup.Item>
      
                  <ListGroup.Item>
                    Description: {product && product.description} // add a check for product here
                  </ListGroup.Item>
                </ListGroup>
              </Col>
              <h1>Pre-orders</h1>
              {preorderItems.length === 0 ? (
                <div className="text-center">
                  <p>Your preorder list is empty</p>
                  <Link to="/" className="btn btn-primary">
                    Go Shopping
                  </Link>
                </div>
              ) : (
                <Row>
                  {preorderItems.map((item) => (
                    <Col key={item.product} xs={12} md={6} lg={4} className="mb-4">
                      <div className="p-3 border rounded">
                        <Image src={item.image} alt={item.name} fluid />
                        <div className="mt-3">
                          <Link
                            to={`/product/${item.product}`}
                            className="fw-bold"
                          >
                            {item.name}
                          </Link>
                        </div>
                        <div className="mb-3">${item.price}</div>
                        <div className="mb-3">Date:{item.preorderdate}</div>
                        <Button
                          variant="danger"
                          onClick={() => removeFromPreorderHandler(item.product)}
                        >
                          Remove
                        </Button>
                      </div>
                    </Col>
                  ))}
                </Row>
              )}
            </>
          )}
        </Container>
      );
      
};

export default PreorderScreen;
