import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage } from '../actions/chatboxActions';

function Chatbox() {
  const [message, setMessage] = useState('');

  const dispatch = useDispatch();

  const chatbox = useSelector(state => state.chatbox);
  const { messages } = chatbox;

  const userInfo = useSelector(state => state.userLogin.userInfo);
  const friends = useSelector(state => state.userLogin.friends);

  const submitHandler = (e) => {
    e.preventDefault();
    if (friends.includes(userInfo.id)) {
      dispatch(addMessage(message));
      setMessage('');
    } else {
      alert('You are not friends with this user!');
    }
  };

  return (
    <div>
      <h2>Chatbox</h2>
      {userInfo ? (
        <form onSubmit={submitHandler}>
          <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
          <button type="submit">Send</button>
        </form>
      ) : (
        <p>Please log in to join the chat.</p>
      )}
      <ul>
        {messages.map((message, index) => (
          <li key={index}>
            <strong>{message.name}</strong>: {message.text}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Chatbox;
