import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import FormContainer from '../components/FormContainer';
import CheckoutSteps from '../components/CheckoutSteps';
import { saveShippingAddress } from '../actions/cartActions';

function ShippingScreen({ history }) {
  const cart = useSelector((state) => state.cart);
  const { ShippingAddress } = cart;

  const dispatch = useDispatch();

  const [address, setAddress] = useState(ShippingAddress.address);
  const [phoneNumber, setPhoneNumber] = useState(ShippingAddress.phoneNumber);
  const [email, setEmail] = useState(ShippingAddress.email);
  const [countryRegion, setCountryRegion] = useState(ShippingAddress.countryRegion);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(saveShippingAddress({ name, phoneNumber, email, countryRegion }));
    history.push('/payment');
  };

  return (
    <FormContainer>
      <CheckoutSteps step1 step2 />
      <h1>Contact Information</h1>
      <Form onSubmit={submitHandler}>
        <Form.Group controlId="name">
          <Form.Label>Name</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="Enter name"
            value={name ? name : ''}
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="phoneNumber">
          <Form.Label>Phone Number</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="Enter phone number"
            value={phoneNumber ? phoneNumber : ''}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            required
            type="email"
            placeholder="Enter email"
            value={email ? email : ''}
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="countryRegion">
          <Form.Label>Country/Region</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="Enter country/region"
            value={countryRegion ? countryRegion : ''}
            onChange={(e) => setCountryRegion(e.target.value)}
          />
        </Form.Group>

        <Button type="submit" variant="primary">
          Continue
        </Button>
      </Form>
    </FormContainer>
  );
}

export default ShippingScreen;
