import axios from 'axios'
import {
    WISH_ADD_ITEM, 
    WISH_REMOVE_ITEM, 
    WISH_SAVE_SHIPPING_ADDRESS, 
    WISH_SAVE_PAYMENT_METHOD, 
    WISH_CLEAR_ITEMS
} from '../constants/wishConstants'

export const addToWishlist = (item) => (dispatch, getState) => {
  const existingItems = getState().wishlist.wishlistItems;
  const newItems = item.filter(
    (item) => !existingItems.some((existingItem) => existingItem.id === item.id)
  );
  dispatch({
    type: WISH_ADD_ITEM,
    payload: [...existingItems, ...newItems],
  });
  localStorage.setItem(
    "wishlistItems",
    JSON.stringify([...existingItems, ...newItems])
  );
};

  
  
  


export const removeFromCart = (id) => (dispatch, getState) => {
    dispatch({
        type: WISH_REMOVE_ITEM,
        payload: id,
    })

    localStorage.setItem('cartItems', JSON.stringify(getState().cart.cartItems))
}


export const saveShippingAddress = (data) => (dispatch) => {
    dispatch({
        type: WISH_SAVE_SHIPPING_ADDRESS,
        payload: data,
    })

    localStorage.setItem('shippingAddress', JSON.stringify(data))
}

export const savePaymentMethod = (data) => (dispatch) => {
    dispatch({
        type: WISH_SAVE_PAYMENT_METHOD,
        payload: data,
    })

    localStorage.setItem('paymentMethod', JSON.stringify(data))
}
