import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongDetails } from '../actions/songActions';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function SongScreen({ match, history}) {
  const [qty, setQty] = useState(1);

  const dispatch = useDispatch();

  const songDetails = useSelector(state => state.songDetails);
  const { loading, error, song } = songDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    dispatch(listSongDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cartsong/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/wishlist/${match.params.id}?qty=${qty}`);
  };

  const addToPreorder = () => {
    history.push(`/preorder/${match.params.id}?qty=${qty}`);
  };
  const [isHovered1, setIsHovered1] = useState(false);
  const [isHovered2, setIsHovered2] = useState(false);

  const handleMouseEnter1 = () => setIsHovered1(true);
  const handleMouseLeave1 = () => setIsHovered1(false);
  const handleMouseEnter2 = () => setIsHovered2(true);
  const handleMouseLeave2 = () => setIsHovered2(false);

  const [currentAudio, setCurrentAudio] = useState(null);
  let audioElement1, audioElement2;
  
  const handlePlayClick = (audioElement) => {
    if (currentAudio) {
      currentAudio.pause();
    }
    setCurrentAudio(audioElement);
    audioElement.play();
  };
  
  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
          <Col
      sm={12}
      md={6}
      lg={4}
      xl={3}
      className="home-screen-product-col"
    >
      <Image src={song.image} alt={song.name} fluid />
      <br />
      <br />
      <Card>
        <ListGroup>
        <ListGroup.Item>
  <Row onMouseEnter={handleMouseEnter2} onMouseLeave={handleMouseLeave2}>
    <h4>{song.title}</h4>
    {song.music && (
      <audio
        controls
        className="music-player"
        style={{ display: isHovered1 ? "block" : "none" }}
        onClick={() => handlePlayClick(audioElement2)}
      >
        <source src={song.music1} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>
    )}
  </Row>
  <audio ref={(element) => { audioElement2 = element; }}>
    </audio>
</ListGroup.Item>


          <ListGroup.Item>
            <Row
              onMouseEnter={handleMouseEnter2}
              onMouseLeave={handleMouseLeave2}
            >
              <h4>{song.title2}</h4>
              {song.music2 && (
                <audio
                  controls
                  className="music-player"
                  style={{ display: isHovered2 ? "block" : "none" }}
                >
                  <source src={song.music2} type="audio/mpeg" />
                  Your browser does not support the audio element.
                </audio>
              )}
            </Row>
          </ListGroup.Item>
        </ListGroup>
      </Card>
    </Col>
     
            <Col md={5}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h1>{song.name}</h1>
                </ListGroup.Item>

                <ListGroup.Item>
                  Description: {song.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup >


                  {song.countInStock > 0 && (
                    <ListGroup.Item>
                      <Row>

                      </Row>
                    </ListGroup.Item>
                  )}

                  <ListGroup.Item>
                    <Button
                      onClick={addToCartHandler}
                      className="btn-block"
                      type="button"
                      disabled={song.countInStock === 0}
                      >
                      Download
                      </Button>
                      </ListGroup.Item>
                      {userInfo ? (
                <ListGroup.Item>
                  <Button
                    onClick={addToWishlist}
                    className="btn-block"
                    type="button"
                    disabled={song.countInStock === 0}
                  >
                    <FontAwesomeIcon icon={faHeart} className="me-2" />
                    Like
                  </Button>
                </ListGroup.Item>
              ) : (
                <ListGroup.Item>
                  <Link to="/login">
                    <Message variant="info">
                      Please login to add to wishlist
                    </Message>
                  </Link>
                </ListGroup.Item>
              )}

              <ListGroup.Item>
                <Button
                  onClick={addToPreorder}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  Add to Playlist
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  )}
</div>

);
}

export default SongScreen;
