import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongDetails } from '../actions/songActions';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function SongScreen({ match, history }) {
  const [qty, setQty] = useState(1);

  const dispatch = useDispatch();

  const songDetails = useSelector(state => state.songDetails);
  const { loading, error, song } = songDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  const audio = document.getElementById('music-player');
const playPauseButton = document.getElementById('play-pause-button');

playPauseButton.addEventListener('click', () => {
  if (audio.paused) {
    audio.play();
    playPauseButton.textContent = 'Pause';
  } else {
    audio.pause();
    playPauseButton.textContent = 'Play';
  }
});

audio.addEventListener('timeupdate', () => {
  const duration = audio.duration;
  const currentTime = audio.currentTime;
  // update your custom duration slider here
});

// add other event listeners for your custom duration slider here

  useEffect(() => {
    dispatch(listSongDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cartsong/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/wishlist/${match.params.id}?qty=${qty}`);
  };

  const addToPreorder = () => {
    history.push(`/preorder/${match.params.id}?qty=${qty}`);
  };

  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
            <Col md={6}>
              
              <Image src={song.image} alt={song.name} fluid />
              <br/>
              <br/>
              
              <h4>{song.title}</h4>
              <audio id="music-player">
  <source src={song.music} type="audio/mp3" />
  Your browser does not support the audio element.
</audio>

<button id="play-pause-button">Play</button>
        <br/>
        <br/>
        <h4>{song.title2}</h4>
              <audio controls className="music-player">
          <source src={song.music2} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>

        <br/>
        <br/>
        <h4>{song.title3}</h4>
              <audio controls className="music-player">
          <source src={song.music3} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>

        <br/>
        <br/>
        <h4>{song.title3}</h4>
              <audio controls className="music-player">
          <source src={song.music3} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>

        <br/>
        <br/>
        <h4>{song.title3}</h4>
<audio controls className="music-player">
  <source src={song.music3} type="audio/mp3" />
  Your browser does not support the audio element.
</audio>

            </Col>

     
            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h1>{song.name}</h1>
                </ListGroup.Item>

                {/* <ListGroup.Item>
                  <Rating
                    value={song.rating}
                    text={`${song.numReviews} reviews`}
                    color={"#f8e825"}
                  />
                </ListGroup.Item> */}

                {/* <ListGroup.Item>Price: ${song.price}</ListGroup.Item> */}

                <ListGroup.Item>
                  Description: {song.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup >
                  {/* <ListGroup.Item>
                    <Row>
                      <Col>Price:</Col>
                      <Col>
                        <strong>${song.price}</strong>
                      </Col>
                    </Row>
                  </ListGroup.Item> */}

                  {/* <ListGroup.Item>
                    <Row>
                      <Col>Status:</Col>
                      <Col>
                        {song.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
                      </Col>
                    </Row>
                  </ListGroup.Item> */}

                  {song.countInStock > 0 && (
                    <ListGroup.Item>
                      <Row>
                        {/* <Col>Qty</Col>
                        <Col>
                          <Form.Control
                            as="select"
                            value={qty}
                            onChange={(e) => setQty(e.target.value)}
                          >
                            {[...Array(song.countInStock).keys()].map((x) => (
                              <option key={x + 1} value={x + 1}>
                                {x + 1}
                              </option>
                            ))}
                          </Form.Control>
                        </Col> */}
                      </Row>
                    </ListGroup.Item>
                  )}

                  <ListGroup.Item>
                    <Button
                      onClick={addToCartHandler}
                      className="btn-block"
                      type="button"
                      disabled={song.countInStock === 0}
                      >
                      Download
                      </Button>
                      </ListGroup.Item>
                      {userInfo ? (
                <ListGroup.Item>
                  <Button
                    onClick={addToWishlist}
                    className="btn-block"
                    type="button"
                    disabled={song.countInStock === 0}
                  >
                    <FontAwesomeIcon icon={faHeart} className="me-2" />
                    Like
                  </Button>
                </ListGroup.Item>
              ) : (
                <ListGroup.Item>
                  <Link to="/login">
                    <Message variant="info">
                      Please login to add to wishlist
                    </Message>
                  </Link>
                </ListGroup.Item>
              )}

              <ListGroup.Item>
                <Button
                  onClick={addToPreorder}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  Add to Playlist
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  )}
</div>

);
}

export default SongScreen;
