import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongDetails } from '../actions/songActions';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { getOrderDetails, payOrder, deliverOrder } from '../actions/orderActions'
import { ORDER_PAY_RESET, ORDER_DELIVER_RESET } from '../constants/orderConstants'
function SongScreen({ match, history}) {


  // const orderId = match.params.id
  const dispatch = useDispatch()


  // const [sdkReady, setSdkReady] = useState(false)

  // const orderDetails = useSelector(state => state.orderDetails)
  // const { order } = orderDetails

  // const orderPay = useSelector(state => state.orderPay)
  // const { loading: loadingPay, success: successPay } = orderPay

  // const orderDeliver = useSelector(state => state.orderDeliver)
  // const { loading: loadingDeliver, success: successDeliver } = orderDeliver

  const userLogin = useSelector(state => state.userLogin)
  const { userInfo } = userLogin


  // if (!loading && !error) {
  //     order.itemsPrice = order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2)
  // }


  // const addPayPalScript = () => {
  //     const script = document.createElement('script')
  //     script.type = 'text/javascript'
  //     script.src = 'https://www.paypal.com/sdk/js?client-id=AdBpHPdb251WJxSmZV3FxvQDXQZN01hrF0albnLIrH5JmIQlCv5eWa46flZdI2pdpLOF22LKgxBE3FSy'
  //     script.async = true
  //     script.onload = () => {
  //         setSdkReady(true)
  //     }
  //     document.body.appendChild(script)
  // }

  // useEffect(() => {

  //     if (!userInfo) {
  //         history.push('/login')
  //     }

  //     if (!order || successPay || order._id !== Number(orderId) || successDeliver) {
  //         dispatch({ type: ORDER_PAY_RESET })
  //         dispatch({ type: ORDER_DELIVER_RESET })

  //         dispatch(getOrderDetails(orderId))
  //     } else if (!order.isPaid) {
  //         if (!window.paypal) {
  //             addPayPalScript()
  //         } else {
  //             setSdkReady(true)
  //         }
  //     }
  // }, [dispatch, order, orderId, successPay, successDeliver])


  // const successPaymentHandler = (paymentResult) => {
  //     dispatch(payOrder(orderId, paymentResult))
  // }

  // const deliverHandler = () => {
  //     dispatch(deliverOrder(order))
  // }

    
  // const download = () => {
  //     window.location.href = order.download;
  //   };
    
    
  const [qty, setQty] = useState(1);
  const [liked, setLiked] = useState(false);

  // const dispatch = useDispatch();

  const songDetails = useSelector(state => state.songDetails);
  const { loading, error, song } = songDetails;

  // const userLogin = useSelector(state => state.userLogin);
  // const { userInfo } = userLogin;

  useEffect(() => {
    dispatch(listSongDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cartsong/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/liked/${match.params.id}?qty=${qty}`);
    setLiked(true); // Update the state to indicate that the user has liked the song

  };

  const addToPreorder = () => {
    history.push(`/playlist/${match.params.id}?qty=${qty}`);
  };
  const [isHovered1, setIsHovered1] = useState(false);
  const [isHovered2, setIsHovered2] = useState(false);
  
  const handleMouseEnter1 = () => setIsHovered1(true);
  const handleMouseLeave1 = () => setIsHovered1(false);
  const handleMouseEnter2 = () => setIsHovered2(true);
  const handleMouseLeave2 = () => setIsHovered2(false);
  
  const [currentAudio, setCurrentAudio] = useState(null);
  let audioElement1, audioElement2;
  
  const handlePlayClick = (audioElement, stopAudioElement) => {
    // Stop the currently playing audio element, if any
    if (stopAudioElement) {
      stopAudioElement.pause();
    }
  
    // Set the new audio element as the currently playing one
    setCurrentAudio(audioElement);
  
    // Play the new audio element
    audioElement.play();
  };
  
  
  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
          <Col
  sm={12}
  md={6}
  lg={4}
  xl={3}
  className="home-screen-product-col"
>
  <Image src={song.image} alt={song.name} fluid />
  <br />
  <br />
  <Card>
    <ListGroup>
      <ListGroup.Item>
        <Row onMouseEnter={handleMouseEnter1} onMouseLeave={handleMouseLeave1}>
          <h4>{song.title}</h4>
          {song.music && (
            <audio
              controls
              className="music-player"
              style={{ display: isHovered1 ? "block" : "none" }}
              onClick={() =>
                handlePlayClick(audioElement1, audioElement2)
              }
            >
              <source src={song.music} type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          )}
        </Row>
        <audio ref={(element) => { audioElement1 = element; }} />
      </ListGroup.Item>
      <ListGroup.Item>
        <Row onMouseEnter={handleMouseEnter2} onMouseLeave={handleMouseLeave2}>
          <h4>{song.title2}</h4>
          {song.music2 && (
            <audio
              controls
              className="music-player"
              style={{ display: isHovered2 ? "block" : "none" }}
              onClick={() =>
                handlePlayClick(audioElement2, audioElement1)
              }
            >
              <source src={song.music2} type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          )}
        </Row>
        <audio ref={(element) => { audioElement2 = element; }} />
      
      </ListGroup.Item>
    </ListGroup>
  </Card>
</Col>
     
            <Col md={5}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h1>{song.name}</h1>
                </ListGroup.Item>

                <ListGroup.Item>
                  Description: {song.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup >


                  {song.countInStock > 0 && (
                    <ListGroup.Item>
                      <Row>

                      </Row>
                    </ListGroup.Item>
                  )}

                  <ListGroup.Item>
           {/* {order.isPaid && order.paymentMethod === 'PayPal' && (
  <Button onClick={download}>Download</Button>
)} */}

                      </ListGroup.Item>
                      {userInfo ? (
                <ListGroup.Item>
                <Button
                  onClick={addToWishlist}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0 || liked} // Disable the button if the song is out of stock or if the user has already liked it
                >
                  <FontAwesomeIcon icon={faHeart} className="me-2" />
                  {liked ? "Liked" : "Like"} {/* Change the button text based on the state */}
                </Button>
              </ListGroup.Item>
              ) : (
                <ListGroup.Item>
                  <Link to="/login">
                    <Message variant="info">
                      Please subscribe to like songs
                    </Message>
                  </Link>
                </ListGroup.Item>
              )}

              <ListGroup.Item>
                <Button
                  onClick={addToPreorder}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  Add to Playlist
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  )}
</div>

);
}

export default SongScreen;
