import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage, addFriend } from '../actions/chatboxActions';

function Chatbox() {
  const [message, setMessage] = useState('');
  const [receiver, setReceiver] = useState(null);
  const [newFriend, setNewFriend] = useState('');

  const dispatch = useDispatch();

  const chatbox = useSelector(state => state.chatbox);
  const { messages } = chatbox;

  const userInfo = useSelector(state => state.userLogin.userInfo);
  const friendsList = useSelector(state => state.friendsList);
  const friends = friendsList.friends ? friendsList.friends : [];

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addMessage(message, receiver));
    setMessage('');
  };

  const selectReceiver = (friendId) => {
    setReceiver(friendId);
  };

  const addFriendHandler = (e) => {
    e.preventDefault();
    dispatch(addFriend({
      _id: new Date().getTime(),
      user: userInfo.id,
      name_of_friend: newFriend
    }));
    setNewFriend('');
  };

  return (
    <div>
      <h2>Chatbox</h2>

      {userInfo ? (
        <form onSubmit={submitHandler}>
          <select value={receiver} onChange={(e) => selectReceiver(e.target.value)}>
            <option value="">Select a friend</option>
            {friends.map((friend) => (
              <option key={friend._id} value={friend._id}>{friend.name_of_friend}</option>
            ))}
          </select>
          <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
          <button type="submit" disabled={!receiver}>Send</button>
        </form>
      ) : (
        <p>Please log in to join the chat.</p>
      )}
      <ul>
        {messages.map((message, index) => (
          <li key={index}>
            <strong>{message.name}</strong>: {message.text}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Chatbox;
