import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongDetails } from '../actions/songActions';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function SongScreen({ match, history}) {
  const [qty, setQty] = useState(1);

  const dispatch = useDispatch();

  const songDetails = useSelector(state => state.songDetails);
  const { loading, error, song } = songDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    dispatch(listSongDetails(match.params.id));
  }, [dispatch, match]);

  const addToCartHandler = () => {
    history.push(`/cartsong/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/wishlist/${match.params.id}?qty=${qty}`);
  };

  const addToPreorder = () => {
    history.push(`/preorder/${match.params.id}?qty=${qty}`);
  };
  const [isHovered1, setIsHovered1] = useState(false);
  const [isHovered2, setIsHovered2] = useState(false);
  const [currentAudioIndex, setCurrentAudioIndex] = useState(0);
  const [audioElements, setAudioElements] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);

  const handleMouseEnter1 = () => setIsHovered1(true);
  const handleMouseLeave1 = () => setIsHovered1(false);
  const handleMouseEnter2 = () => setIsHovered2(true);
  const handleMouseLeave2 = () => setIsHovered2(false);

  const songs = [
    {
      title: "Song 1",
      music: "https://example.com/song1.mp3",
    },
    {
      title: "Song 2",
      music: "https://example.com/song2.mp3",
    },
    {
      title: "Song 3",
      music: "https://example.com/song3.mp3",
    },
  ];

  useEffect(() => {
    const newAudioElements = songs.map(() => new Audio());
    setAudioElements(newAudioElements);

    return () => {
      newAudioElements.forEach((audio) => audio.pause());
    };
  }, [songs]);

  const handlePlayClick = (index) => {
    const audio = audioElements[index];
    if (currentAudioIndex === index && isPlaying) {
      audio.paused();
      setIsPlaying(false);
    } else {
      if (currentAudioIndex !== index && isPlaying) {
        audioElements[currentAudioIndex].paused();
      }
      setCurrentAudioIndex(index);
      audio.addEventListener("canplay", () => {
        setIsPlaying(true);
        audio.play();
      });
    }
  };
  

  const handleNextClick = () => {
    const nextIndex = (currentAudioIndex + 1) % songs.length;
    handlePlayClick(nextIndex);
  };

  const handlePrevClick = () => {
    const prevIndex = (currentAudioIndex - 1 + songs.length) % songs.length;
    handlePlayClick(prevIndex);
  };
  
  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
          <Col sm={12} md={6} lg={4} xl={3} className="home-screen-product-col">
        <Image src={song.image} alt={song.name} fluid />
        <br />
        <br />
        <Card>
          <ListGroup>
            {songs.map((song, index) => (
              <ListGroup.Item key={index}>
                <Row
                  onMouseEnter={
                    index === 0 ? handleMouseEnter1 : handleMouseEnter2
                  }
                  onMouseLeave={
                    index === 0 ? handleMouseLeave1 : handleMouseLeave2
                  }
                >
                  <h4>{song.title}</h4>
                  {song.music && (
                    <div>
                      <audio
                        ref={(element) => {
                          audioElements[index] = element;
                        }}
                        className="music-player"
                        style={{ display: "none" }}
                      >
                        <source src={song.music} type="audio/mpeg" />
                        Your browser does not support the audio element.
                      </audio>
                      <button
                        onClick={() => handlePrevClick()}
                        disabled={index === 0}
                      >
                        Prev
                      </button>
                      <button
                        onClick={() => handlePlayClick(index)}
                        disabled={audioElements[index].paused ? false : true}
                      >
                        {audioElements[index].paused ? "Play" : "Pause"}
                      </button>
                      <button
                        onClick={() => handleNextClick()}
                        disabled={index === songs.length - 1}
                        >
                          Next
                        </button>
                      </div>
                    )}
                  </Row>
                </ListGroup.Item>
              ))}
            </ListGroup>
          </Card>
        </Col>
     
            <Col md={5}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h1>{song.name}</h1>
                </ListGroup.Item>

                <ListGroup.Item>
                  Description: {song.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup >


                  {song.countInStock > 0 && (
                    <ListGroup.Item>
                      <Row>

                      </Row>
                    </ListGroup.Item>
                  )}

                  <ListGroup.Item>
                    <Button
                      onClick={addToCartHandler}
                      className="btn-block"
                      type="button"
                      disabled={song.countInStock === 0}
                      >
                      Download
                      </Button>
                      </ListGroup.Item>
                      {userInfo ? (
                <ListGroup.Item>
                  <Button
                    onClick={addToWishlist}
                    className="btn-block"
                    type="button"
                    disabled={song.countInStock === 0}
                  >
                    <FontAwesomeIcon icon={faHeart} className="me-2" />
                    Like
                  </Button>
                </ListGroup.Item>
              ) : (
                <ListGroup.Item>
                  <Link to="/login">
                    <Message variant="info">
                      Please login to add to wishlist
                    </Message>
                  </Link>
                </ListGroup.Item>
              )}

              <ListGroup.Item>
                <Button
                  onClick={addToPreorder}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  Add to Playlist
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  )}
</div>

);
}

export default SongScreen;
