import React, { useEffect } from 'react';
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import CheckoutSteps from '../components/CheckoutSteps';
import { createPlan } from '../actions/planActions';
import { PLAN_CREATE_RESET } from '../constants/planConstants';

function PlaceOrderScreen({ history }) {
  const dispatch = useDispatch();

  const cart = useSelector((state) => state.cart);
  const { cartPlans, paymentMethod, shippingAddress } = cart;

//   const planPrice = cartPlans.reduce((acc, plan) => acc + plan.price * plan.qty, 0).toFixed(2);
//   const taxPrice = Number(0.082 * planPrice).toFixed(2);
//   const totalPrice = (Number(planPrice) + Number(taxPrice)).toFixed(2);

  const placeOrderHandler = () => {
    dispatch(
      createPlan({
        orderPlans: cartPlans,
        paymentMethod,
        planPrice,
        taxPrice,
        totalPrice,
      })
    );
  };

  const orderCreate = useSelector((state) => state.orderCreate);
  const { order, success, error } = orderCreate;

  useEffect(() => {
    if (success) {
      history.push(`/plan/${order._id}`);
      dispatch({ type: PLAN_CREATE_RESET });
    }
  }, [dispatch, history, order, success]);

  if (!paymentMethod) {
    history.push('/payment');
  }

  return (
    <>
      <CheckoutSteps step1 step2 step3 step4 />
      <Row>
        <Col md={8}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>Contact Details</h2>
              <p>
                <strong>Address:</strong> {shippingAddress.address}, {shippingAddress.city}{' '}
                {shippingAddress.postalCode}, {shippingAddress.country}
              </p>
            </ListGroup.Item>

            <ListGroup.Item>
              <h2>Payment Method</h2>
              <p>
                <strong>Method:</strong> {paymentMethod}
              </p>
            </ListGroup.Item>

            <ListGroup.Item>
              <h2>Order Plans</h2>
              {cartPlans.length === 0 ? (
                <Message variant="info">Your cart is empty</Message>
              ) : (
                <ListGroup variant="flush">
                  {cartPlans.map((plan, index) => (
                    <ListGroup.Item key={index}>
                      <Row>
                        <Col md={2}>
                          <Image src={plan.image} alt={plan.name} fluid rounded />
                        </Col>
                        <Col>
                          <Link to={`/plan/${plan.plan}`}>{plan.name}</Link>
                        </Col>
                        <Col md={4}>
                          {plan.qty} x ${plan.price} = ${(plan.qty * plan.price).toFixed(2)}
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={4}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h2>Order Summary</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Plans</Col>
                  <Col>${planPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Tax</Col>
                  <Col>${taxPrice}</Col>
                  </Row>
        </ListGroup.Item>
        <ListGroup.Item>
          <Row>
            <Col>Total</Col>
            <Col>${totalPrice}</Col>
          </Row>
        </ListGroup.Item>
        <ListGroup.Item>
          {error && <Message variant="danger">{error}</Message>}
        </ListGroup.Item>
        <ListGroup.Item>
          <Button type="button" className="btn-block" disabled={cartPlans.length === 0} onClick={placeOrderHandler}>
            Place Order
          </Button>
        </ListGroup.Item>
      </ListGroup>
    </Card>
  </Col>
</Row>
</>
);
}

export default PlaceOrderScreen;
