import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addToPreorder, removeFromPreorder } from '../actions/preorderActions';
import { Row, Col, Image, ListGroup, Button, Card, Form, Container } from 'react-bootstrap';
// import { Card } from 'react-bootstrap'
import { listProductDetails, createProductReview } from '../actions/productActions';
import Rating from '../components/Rating';

const PreorderScreen = ({ match, location, history }) => {
    const productId = match.params.id;
    const qty = location.search ? Number(location.search.split('=')[1]) : 1;
    const dispatch = useDispatch();

    const preorder = useSelector(state => state.preorder);
    const { preorderItems } = preorder;

    useEffect(() => {
        if (productId) {
            dispatch(addToPreorder(productId, qty));

        }
    }, [dispatch, productId, qty]);

    const removeFromPreorderHandler = (id) => {
        dispatch(removeFromPreorder(id));
    };

    const productDetails = useSelector(state => state.productDetails);
    const { loading, error, product } = productDetails;

    return (
        <Container>
            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h3>{product.name}</h3>
                </ListGroup.Item>

                <ListGroup.Item>
                  <Rating
                    value={product.rating}
                    text={`${product.numReviews} reviews`}
                    color={"#f8e825"}
                  />
                </ListGroup.Item>

                <ListGroup.Item>Price: ${product.price}</ListGroup.Item>

                <ListGroup.Item>
                  Description: {product.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>
            <h1>Pre-orders</h1>
            {preorderItems.length === 0 ? (
                <div className="text-center">
                    <p>Your preorder list is empty</p>
                    <Link to='/' className="btn btn-primary">Go Shopping</Link>
                </div>
            ) : (
                <Row>
                    {preorderItems.map(item => (
    <Col key={item.product} xs={12} md={6} lg={4} className="mb-4">
        <div className="p-3 border rounded">
            <Image src={item.image} alt={item.name} fluid />
            <div className="mt-3">
                <Link to={`/product/${item.product}`} className="fw-bold">{item.name}</Link>
            </div>
            <div className="mb-3">${item.price}</div>
            <div className="mb-3">Date:{item.preorderdate}</div>
            <Button variant="danger" onClick={() => removeFromPreorderHandler(item.product)}>Remove</Button>
        </div>
    </Col>
))}



                </Row>
            )}
        </Container>
    );
};

export default PreorderScreen;
