import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchFriends, addFriend } from '../actions/friendlistActions';

const FriendList = ([ friend ]) => {
  const { friends, loading, error } = useSelector(state => state.friendList);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchFriends());
  }, [dispatch]);

  const handleAddFriend = (e) => {
    e.preventDefault();
    const friendId = parseInt(e.target.elements.friendId.value);
    dispatch(addFriend(friendId));
    e.target.reset();
  };

  return (
    <div>
      <h2>Friend List</h2>
      {loading && <p>Loading friends...</p>}
      {error && <p>Error fetching friends: {error.message}</p>}
      {friends && Array.isArray(friends) && friends.length > 0 ? (

        <ul>
          {friends.map(friend => (
            <li key={friend.id}>{friend.username}</li>
          ))}
        </ul>
      ) : (
        <p>No friends to display</p>
      )}
      <form onSubmit={handleAddFriend}>
        <label htmlFor="friendId">Add Friend:</label>
        <input type="text" name="friendId" />
        <button type="submit">Add</button>
      </form>
    </div>
  );
};

export default FriendList;
