import React, { useState } from 'react';
import { Form, Button, Col } from 'react-bootstrap';
import { useDispatch } from 'react-redux';
import FormContainer from '../components/FormContainer';
import CheckoutSteps from '../components/CheckoutSteps';
import { plansavePaymentMethod } from '../actions/plancartActions';

function PaymentScreen({ history }) {
  const dispatch = useDispatch();

  const [planpaymentMethod, plansetPaymentMethod] = useState('PayPal');

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(plansavePaymentMethod(planpaymentMethod));
    history.push('/planplaceorder');
  };

  return (
    <FormContainer>
      {/* <CheckoutSteps step1 step2 step3 /> */}

      <Form onSubmit={submitHandler}>
        <Form.Group>
          <Form.Label as='legend'>Select Payment Method</Form.Label>
          <Col>
            <Form.Check
              type='radio'
              label='PayPal or Credit Card'
              id='paypal'
              name='paymentMethod'
              checked
              onChange={(e) => plansetPaymentMethod(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Button type='submit' variant='primary'>
          Continue
        </Button>
      </Form>
    </FormContainer>
  );
}

export default PaymentScreen;
