import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import {
listSongDetails,
downloadSong,
} from '../actions/songActions';

function SongScreen({ match, history, isPaid }) {
const [qty, setQty] = useState(1);
const [liked, setLiked] = useState(false);

const songDetails = useSelector((state) => state.songDetails);
const { song } = songDetails;

const dispatch = useDispatch();

useEffect(() => {
dispatch(listSongDetails(match.params.id));
}, [dispatch, match]);

const addToCartHandler = () => {
history.push(`/cartsong/${match.params.id}?qty=${qty}`);
};

const addToWishlist = () => {
history.push(`/liked/${match.params.id}?qty=${qty}`);
setLiked(true);
};

const addToPreorder = () => {
history.push(`/playlist/${match.params.id}?qty=${qty}`);
};

const downloadHandler = () => {
dispatch(downloadSong(match.params.id));
};

if (!song) {
return <Loader />;
}

return (
<>

<Link className='btn btn-light my-3' to='/'>
Go Back
</Link>
<Row>
<Col md={6}>
<Image src={song.image} alt={song.title} fluid />
</Col>
<Col md={3}>
<ListGroup variant='flush'>
<ListGroup.Item>
<h3>{song.title}</h3>
</ListGroup.Item>
<ListGroup.Item>
<Rating
value={song.rating}
text={`${song.numReviews} reviews`}
/>
</ListGroup.Item>
<ListGroup.Item>Price: ${song.price}</ListGroup.Item>
<ListGroup.Item>Description: {song.description}</ListGroup.Item>
</ListGroup>
</Col>
<Col md={3}>
<Card>
<ListGroup variant='flush'>
<ListGroup.Item>
<Row>
<Col>Price:</Col>
<Col>
<strong>${song.price}</strong>
</Col>
</Row>
</ListGroup.Item>
<ListGroup.Item>
<Row>
<Col>Status:</Col>
<Col>
{song.countInStock > 0 ? 'In Stock' : 'Out Of Stock'}
</Col>
</Row>
</ListGroup.Item>
{isPaid ? (
<>
<ListGroup.Item>
<Row>

<Col>Qty</Col>
<Col>
<Form.Control
as='select'
value={qty}
onChange={(e) => setQty(e.target.value)}
>
{[...Array(song.countInStock).keys()].map((x) => (
<option key={x + 1} value={x + 1}>
{x + 1}
</option>
))}
</Form.Control>
</Col>
</Row>
</ListGroup.Item>
<ListGroup.Item>
<Button
onClick={addToCartHandler}
className='btn-block'
type='button'
disabled={song.countInStock === 0}
>
Add To Cart
</Button>
</ListGroup.Item>
<ListGroup.Item>
<Button
onClick={addToWishlist}
className='btn-block'
type='button'
disabled={liked}
>
{liked ? 'Added to Wishlist' : 'Add to Wishlist'}
</Button>
</ListGroup.Item>
<ListGroup.Item>
<Button
onClick={addToPreorder}
className='btn-block'
type='button'
disabled={song.countInStock === 0}
>
Pre-order
</Button>
</ListGroup.Item>
<ListGroup.Item>
<Button
onClick={downloadHandler}
className='btn-block'
type='button'
>
Download Song
</Button>
</ListGroup.Item>
</>
) : (
<>
<Message variant='warning'>
Please purchase the song to access all features.
</Message>
<Link to={`/payment/${match.params.id}`}>Buy Now</Link>
</>
)}
</ListGroup>
</Card>
</Col>
</Row>
</>
);
}
export default SongScreen;
