import React, { useState, useEffect } from 'react';
import { Row, Col } from 'react-bootstrap';
import Song from '../components/Song';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongs } from '../actions/songActions';
import { useDispatch, useSelector } from 'react-redux';
import SongCarousel2 from './SongCarousel';

function SongListScreen() {
  const dispatch = useDispatch();
  const songList = useSelector((state) => state.songList);
  const { error, loading, songs, page, pages } = songList;

  useEffect(() => {
    dispatch(listSongs());
  }, [dispatch]);

  return (
    <div className="home-screen-container">

      <SongCarousel2 />
      <h3>Listen Music</h3>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Row className="home-screen-product-list">
          {songs.map((song) => (
            <Col
              key={song._id}
              sm={12}
              md={6}
              lg={4}
              xl={3}
              className="home-screen-product-col"
            >
              <Song song={song} />
            </Col>
          ))}
        </Row>
      )}
    </div>
  );
}

export default SongListScreen;
