import { ADD_MESSAGE } from '../constants/chatboxConstants';

export const addMessage = (text, receiver) => (dispatch, getState) => {
  const userInfo = getState().userLogin.userInfo;
  const sender = userInfo ? userInfo.id : null;
  
  // Check if sender and receiver are friends or the same user
  const friends = getState().friendsList.friends;
  const isFriend = friends.some(friend => friend._id === receiver && friend.user === sender);
  const isUser = sender === receiver;
  
  if (isFriend || isUser) {
    const name = userInfo ? userInfo.name : 'Anonymous';
    dispatch({
      type: ADD_MESSAGE,
      payload: { name, text, sender, receiver }
    });
  }
};

function Chatbox() {
  const [message, setMessage] = useState('');
  const [receiver, setReceiver] = useState(null);

  const dispatch = useDispatch();

  const chatbox = useSelector(state => state.chatbox);
  const { messages } = chatbox;

  const userInfo = useSelector(state => state.userLogin.userInfo);
  const friendsList = useSelector(state => state.friendsList);
  const { friends } = friendsList;

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addMessage(message, receiver));
    setMessage('');
  };

  const selectReceiver = (friendId) => {
    setReceiver(friendId);
  };

  return (
    <div>
      <h2>Chatbox</h2>
      {userInfo ? (
        <form onSubmit={submitHandler}>
          <select value={receiver} onChange={(e) => selectReceiver(e.target.value)}>
            <option value="">Select a friend</option>
            {friends.map((friend) => (
              <option key={friend._id} value={friend._id}>{friend.name_of_friend}</option>
            ))}
          </select>
          <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
          <button type="submit" disabled={!receiver}>Send</button>
        </form>
      ) : (
        <p>Please log in to join the chat.</p>
      )}
      <ul>
        {messages.map((message, index) => (
          <li key={index}>
            <strong>{message.name}</strong>: {message.text}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Chatbox;
