import React from 'react';
import { Card } from 'react-bootstrap';
import Rating from './Rating';
import { Link } from 'react-router-dom';
import './Product.css'

function Product({ product }) {
  return (
    <div className="text-center">
      <Card className={`my-3 p-3 rounded bg-light text-black ${product.countInStock === 0 ? 'disabled' : ''}`}>
        <Link to={`/product/${product._id}`}>
          <Card.Img src={product.image} variant="top" />
        </Link>

        <Card.Body>
          <Link style={{ color: 'black', textDecoration: 'none' }} to={`/product/${product._id}`}>
            <Card.Title as="h3">
              <strong>{product.name}</strong>
            </Card.Title>
          </Link>

          <Card.Text as="h3" className="mb-3">
            ${product.price}
          </Card.Text>
          <Card.Text as="h3" className="mb-3">
            {product.countInStock === 0 ? 'Out of Stock' : `In Stock: ${product.countInStock}`}
          </Card.Text>

          <Link to={`/product/${product._id}`} className={`btn btn-secondary ${product.countInStock === 0 ? 'disabled' : ''}`} disabled={product.countInStock === 0}>
            {product.countInStock === 0 ? 'Out of Stock' : 'View Details'}
          </Link>

        </Card.Body>
      </Card>
    </div>
  );
}

export default Product;
