import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage, addFriend } from '../actions/chatboxActions';

function Chatbox() {
  const [message, setMessage] = useState('');
  const [receiver, setReceiver] = useState('');
  const [newFriend, setNewFriend] = useState('');

  const dispatch = useDispatch();

  const chatbox = useSelector(state => state.chatbox);
  const { messages } = chatbox;

  const userInfo = useSelector(state => state.userLogin.userInfo);
  const friendsList = useSelector(state => state.friendsList);
  const { friends } = friendsList;

  // Check if user is not in friends list, and add if necessary
  if (!friends.some(friend => friend.user === userInfo._id)) {
    dispatch(addFriend({
      _id: new Date().getTime(),
      user: userInfo._id,
      name_of_friend: userInfo.name // Or whatever property contains the user's name
    }));
  }

  const submitHandler = (e) => {
    e.preventDefault();
    if (receiver !== null) {
      dispatch(addMessage(message, receiver));
      setMessage('');
    }
  };

  const selectReceiver = (friendId) => {
    setReceiver(friendId);
  };

  const addFriendHandler = (e) => {
    e.preventDefault();
    dispatch(addFriend({
      _id: new Date().getTime(),
      user: userInfo._id,
      name_of_friend: newFriend
    }));
    setNewFriend('');
  };

  return (
    <div>
      <h2>Chatbox</h2>
      <div className="friend-list">
        <h3>Friends</h3>
        <ul>
          {friends.map((friend) => (
            <li key={friend._id} onClick={() => selectReceiver(friend._id)}>
              {friend.name_of_friend}
            </li>
          ))}
        </ul>
        <form onSubmit={addFriendHandler}>
          <input
            type="text"
            placeholder="Add a friend"
            value={newFriend}
            onChange={(e) => setNewFriend(e.target.value)}
          />
          <button type="submit">Add</button>
        </form>
      </div>
      <div className="message-box">
        <h3>Messages</h3>
        <ul>
          {messages.map((message) => (
            <li key={message._id}>
              <strong>{message.sender}</strong>: {message.text}
            </li>
          ))}
        </ul>
        <form onSubmit={submitHandler}>
          <input
            type="text"
            placeholder="Type your message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
}

export default Chatbox;
