import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
// import { Link } from 'react-router-dom'
import { ListGroup, Button } from 'react-bootstrap'
import { addToWishlist } from '../actions/wishActions'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart as farHeart, faHeart as fasHeart } from '@fortawesome/free-regular-svg-icons'

function Wishhandler({ productId }) {
  const dispatch = useDispatch()

  const wishlistItems = useSelector(state => state.wishlist?.wishlistItems ?? [])
  const isProductInWishlist = wishlistItems.find(item => item.product === productId)

  const handleAddToWishlist = () => {
    dispatch(addToWishlist(productId))
    history.push(`/wishlist/${match.params.id}?qty=${qty}`)  }

  return (
    <ListGroup.Item>
      <Button onClick={handleAddToWishlist} className='btn btn-light my-3' type='button'>
        <FontAwesomeIcon icon={isProductInWishlist ? fasHeart : farHeart} /> Wishlist
      </Button>
    </ListGroup.Item>
  )
}

export default Wishhandler
