import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap'
// import Rating from '../components/Rating'
// import Loader from '../components/Loader'
// import Message from '../components/Message'
import { listProductDetails, createProductReview } from '../actions/productActions'
import { PRODUCT_CREATE_REVIEW_RESET } from '../constants/productConstants'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart as farHeart } from '@fortawesome/free-regular-svg-icons'
// import Wishhandler from './Wishhandler'

function Wishhandler({ match, history}) {
    const [qty, setQty] = useState(1)
    const [rating, setRating] = useState(0)
    const [comment, setComment] = useState('')
    const [wishlist, setWishlist] = useState([])

    const dispatch = useDispatch()

    const productDetails = useSelector(state => state.productDetails)
    const { loading, error, product } = productDetails

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin

    const productReviewCreate = useSelector(state => state.productReviewCreate)
    const {
        loading: loadingProductReview,
        error: errorProductReview,
        success: successProductReview,
    } = productReviewCreate

    // useEffect(() => {
    //     if (successProductReview) {
    //         setRating(0)
    //         setComment('')
    //         dispatch({ type: PRODUCT_CREATE_REVIEW_RESET })
    //     }

    //     dispatch(listProductDetails(match.params.id))

    // }, [dispatch, match, successProductReview])

    const addToCartHandler = () => {
        history.push(`/cart/${match.params.id}?qty=${qty}`)
    }

    const addWishlistHandler = () => {
        const existingWishlist = JSON.parse(localStorage.getItem('wishlist')) || []
        const newWishlist = [...existingWishlist, { id: product._id, name: product.name, image: product.image, price: product.price }]
        localStorage.setItem('wishlist', JSON.stringify(newWishlist))
        setWishlist(newWishlist)
        
    }

    const deleteWishlistHandler = () => {
        localStorage.removeItem('wishlist')
        setWishlist([])
    }
  return (
    <div>Wishhandler
           <ListGroup.Item>
  <Link to={`/wishlist/${match.params.id}?qty=${qty}`}>
    <Button onClick={() => addWishlistHandler()} className='btn btn-light my-3' type='button' icon={farHeart}>
      Wishlist
    </Button>
  </Link>
</ListGroup.Item>
    </div>
  )
}

export default Wishhandler
