import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form, ListGroupItem } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listSongDetails, createSongReview } from '../actions/songActions';
import { SONG_CREATE_REVIEW_RESET } from '../constants/songConstants';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function SongScreen({ match, history }) {
  const [qty, setQty] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [wishlist, setWishlist] = useState([]);

  const dispatch = useDispatch();

  const songDetails = useSelector(state => state.songDetails);
  const { loading, error, song } = songDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  const songReviewCreate = useSelector(state => state.songReviewCreate);
  const {
    loading: loadingSongReview,
    error: errorSongReview,
    success: successSongReview,
  } = songReviewCreate;

  useEffect(() => {
    if (successSongReview) {
      setRating(0);
      setComment('');
      dispatch({ type: SONG_CREATE_REVIEW_RESET });
    }

    dispatch(listSongDetails(match.params.id));
  }, [dispatch, match, successSongReview]);

  const addToCartHandler = () => {
    history.push(`/cartsong/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/wishlist/${match.params.id}?qty=${qty}`);
  };

  const addToPreorder = () => {
    history.push(`/preorder/${match.params.id}?qty=${qty}`);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(createSongReview(
        match.params.id, {
        rating,
        comment
    }
    ));
  };

  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
            <Col md={6}>
              <Image src={song.image} alt={song.name} fluid />
            </Col>

          

            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h3>{song.name}</h3>
                </ListGroup.Item>

                <ListGroup.Item>
                  <Rating
                    value={song.rating}
                    text={`${song.numReviews} reviews`}
                    color={"#f8e825"}
                  />
                </ListGroup.Item>

                <ListGroup.Item>Price: ${song.price}</ListGroup.Item>

                <ListGroup.Item>
                  Description: {song.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroup.Item>
                   

                                                <Row>
                                                    <Col>Price:</Col>
                                                    <Col>
                                                        <strong>${song.price}</strong>
                                                    </Col>
                                                </Row>
                                            </ListGroup.Item>
                                            <ListGroup.Item>
                <Row>
                <Col>Status:</Col>
                  <Col>
                    {song.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
                  </Col>
                </Row>
              </ListGroup.Item>

              {song.countInStock > 0 && (
                <ListGroup.Item>
                  <Row>
                    <Col>Qty</Col>
                    <Col>
                      <Form.Control
                        as="select"
                        value={qty}
                        onChange={(e) => setQty(e.target.value)}
                      >
                        {[...Array(song.countInStock).keys()].map((x) => (
                          <option key={x + 1} value={x + 1}>
                            {x + 1}
                          </option>
                        ))}
                      </Form.Control>
                    </Col>
                  </Row>
                </ListGroup.Item>
              )}

              <ListGroup.Item>
                <Button
                  onClick={addToCartHandler}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  Add to Cart
                </Button>
              </ListGroup.Item>

              <ListGroup.Item>
                <Button
                  onClick={addToWishlist}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  <FontAwesomeIcon icon={faHeart} /> Add to Wishlist
                </Button>
              </ListGroup.Item>

              <ListGroup.Item>
                <Button
                  onClick={addToPreorder}
                  className="btn-block"
                  type="button"
                  disabled={song.countInStock === 0}
                >
                  Pre-Order Now
                </Button>
              </ListGroup.Item>

              <ListGroup.Item>
                <h4>Write a review</h4>
                {loadingSongReview && <Loader />}
                {successSongReview && (
                  <Message variant="success">
                    Review submitted successfully
                  </Message>
                )}
                {errorSongReview && (
                  <Message variant="danger">{errorSongReview}</Message>
                )}
                {userInfo ? (
                  <Form onSubmit={submitHandler}>
                    <Form.Group controlId="rating">
                      <Form.Label>Rating</Form.Label>
                      <Form.Control
                        as="select"
                        value={rating}
                        onChange={(e) => setRating(e.target.value)}
                      >
                        <option value="">Select...</option>
                        <option value="1">1 - Poor</option>
                        <option value="2">2 - Fair</option>
                        <option value="3">3 - Good</option>
                        <option value="4">4 - Very Good</option>
                        <option value="5">5 - Excellent</option>
                      </Form.Control>
                    </Form.Group>
                    <Form.Group controlId="comment">
                      <Form.Label>Comment</Form.Label>
                      <Form.Control
                        as="textarea"
                        row="3"
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                      ></Form.Control>
                    </Form.Group>
                    <Button type="submit" variant="primary">
                      Submit
                    </Button>
                  </Form>
                ) : (
                  <Message>
                    Please <Link to="/login">sign in</Link> to write a review{' '}
                  </Message>
                )}
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  )}
</div>
);
}

export default SongScreen;
