import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form, ListGroupItem } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { listPlanDetails, createPlanReview } from '../actions/planActions';
import { PLAN_CREATE_REVIEW_RESET } from '../constants/planConstants';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function PlanScreen({ match, history }) {
  const [qty, setQty] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [wishlist, setWishlist] = useState([]);

  const dispatch = useDispatch();

  const planDetails = useSelector(state => state.planDetails);
  const { loading, error, plan } = planDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  const planReviewCreate = useSelector(state => state.planReviewCreate);
  const {
    loading: loadingPlanReview,
    error: errorPlanReview,
    success: successPlanReview,
  } = planReviewCreate;

  useEffect(() => {
    if (successPlanReview) {
      setRating(0);
      setComment('');
      dispatch({ type: PLAN_CREATE_REVIEW_RESET });
    }

    dispatch(listPlanDetails(match.params.id));
  }, [dispatch, match, successPlanReview]);

  const addToCartHandler = () => {
    history.push(`/cartplan/${match.params.id}?qty=${qty}`);
  };

  const addToWishlist = () => {
    history.push(`/wishlist/${match.params.id}?qty=${qty}`);
  };

  const addToPreorder = () => {
    history.push(`/preorder/${match.params.id}?qty=${qty}`);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(createPlanReview(
        match.params.id, {
        rating,
        comment
    }
    ));
  };

  return (
    <div>
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div>
          <Row>
            <Col md={6}>
              <Image src={plan.image} alt={plan.name} fluid />
            </Col>

          

            <Col md={3}>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <h3>{plan.name}</h3>
                </ListGroup.Item>

                <ListGroup.Item>
                  <Rating
                    value={plan.rating}
                    text={`${plan.numReviews} reviews`}
                    color={"#f8e825"}
                  />
                </ListGroup.Item>

                <ListGroup.Item>Price: ${plan.price}</ListGroup.Item>

                <ListGroup.Item>
                  Description: {plan.description}
                </ListGroup.Item>
              </ListGroup>
            </Col>

            <Col md={3}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroup.Item>
                   

                                                <Row>
                                                    <Col>Price:</Col>
                                                    <Col>
                                                        <strong>${plan.price}</strong>
                                                    </Col>
                                                </Row>
                                            </ListGroup.Item>
                                            <ListGroup.Item>
                  <Row>
                    <Col>Status</Col>
<Col>
{plan.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
</Col>
</Row>
</ListGroup.Item>{plan.countInStock > 0 && (
              <ListGroup.Item>
                <Row>
                  <Col>Qty</Col>
                  <Col>
                    <Form.Control
                      as="select"
                      value={qty}
                      onChange={(e) => setQty(e.target.value)}
                    >
                      {[...Array(plan.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </Form.Control>
                  </Col>
                </Row>
              </ListGroup.Item>
            )}

            <ListGroup.Item>
              <Button
                onClick={addToCartHandler}
                className="btn-block"
                type="button"
                disabled={plan.countInStock === 0}
              >
                Add to Cart
              </Button>
            </ListGroup.Item>

            <ListGroup.Item>
              <Button
                onClick={addToWishlist}
                className="btn-block"
                type="button"
                disabled={plan.countInStock === 0}
              >
                <FontAwesomeIcon icon={faHeart} style={{color:'#ff7f50'}} />
                &nbsp;Add to Wishlist
              </Button>
            </ListGroup.Item>

            <ListGroup.Item>
              <Button
                onClick={addToPreorder}
                className="btn-block"
                type="button"
                disabled={plan.countInStock === 0}
              >
                Pre-Order
              </Button>
            </ListGroup.Item>

            <ListGroupItem>
              {loadingPlanReview && <Loader />}
              {successPlanReview && (
                <Message variant="success">
                  Review submitted successfully
                </Message>
              )}
              {errorPlanReview && (
                <Message variant="danger">{errorPlanReview}</Message>
              )}

              {userInfo ? (
                <Form onSubmit={submitHandler}>
                  <Form.Group controlId="rating">
                    <Form.Label>Rating</Form.Label>
                    <Form.Control
                      as="select"
                      value={rating}
                      onChange={(e) => setRating(e.target.value)}
                    >
                      <option value="">Select...</option>
                      <option value="1">1 - Poor</option>
                      <option value="2">2 - Fair</option>
                      <option value="3">3 - Good</option>
                      <option value="4">4 - Very Good</option>
                      <option value="5">5 - Excellent</option>
                    </Form.Control>
                  </Form.Group>

                  <Form.Group controlId="comment">
                    <Form.Label>Comment</Form.Label>
                    <Form.Control
                      as="textarea"
                      row="3"
                      value={comment}
                      onChange={(e) => setComment(e.target.value)}
                    ></Form.Control>
                  </Form.Group>

                  <Button type="submit" variant="primary">
                    Submit
                  </Button>
                </Form>
              ) : (
                <Message>
                  Please <Link to="/login">sign in</Link> to write a review{' '}
                </Message>
              )}
            </ListGroupItem>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  )}
</div>
);
}

export default PlanScreen;
