import React from 'react';
import { Card } from 'react-bootstrap';
import Rating from './Rating';
import { Link } from 'react-router-dom';

function plan({ plan}) {
  return (
    <Card className="my-3 p-3 rounded bg-dark text-white">
      <Link to={`/plan/${plan._id}`}>
        <Card.Img src={plan.image} variant="top" />
      </Link>

      <Card.Body>
        <Link to={`/plan/${plan._id}`}>
          <Card.Title as="div">
            <strong>{plan.name}</strong>
          </Card.Title>
        </Link>

        <Card.Text as="div">
          <div className="my-3">
            <Rating
              value={plan.rating}
              text={`${plan.numReviews} reviews`}
              color={'#f8e825'}
            />
          </div>
        </Card.Text>

        <Card.Text as="h3" className="mb-3">
          ${plan.price}
        </Card.Text>

        <Card.Text as="h5" className="mb-3">
  {plan.countInStock === 0 ? "Out of Stock" : `Available`}
</Card.Text>


        <Link to={`/plan/${plan._id}`} className="btn btn-primary">
          View Details
        </Link>
      </Card.Body>
    </Card>
  );
}

export default plan;
