import {PLAYLIST_CREATE_REQUEST, PLAYLIST_CREATE_SUCCESS, PLAYLIST_CREATE_FAIL} from '../constants/playlistConstants'

export const playlistReducer = (state = {}, action) => {
    switch (action.type) {
      case PLAYLIST_CREATE_REQUEST:
        return { loading: true };
      case PLAYLIST_CREATE_SUCCESS:
        return { loading: false, playlist: action.payload };
      case PLAYLIST_CREATE_FAIL:
        return { loading: false, error: action.payload };
      default:
        return state;
    }
  };
